import { StackScreenProps } from '@react-navigation/stack';
import * as React from 'react';
import { Button, StyleSheet, Image, TouchableOpacity, View, Text } from 'react-native';
import Colors from '../constants/Colors';
import Drawer from 'react-native-drawer';

import { RootStackParamList } from '../types';
import { Component } from 'react';

export default class HomeScreen extends Component {

  private drawer: Drawer | null = null;
  private navigation: any;

  constructor(props: StackScreenProps<RootStackParamList, 'Home'>) {
    super(props);
    this.navigation = props.navigation;


  }

  render() {
    return (
      <Drawer
          type="overlay"
          ref={(ref) => this.drawer = ref}
          content={
            <View style={styles.sideBarParent}>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.sidebarbutton}
                onPress={_ => this.navigation.push('Settings')}

              >
                <Text style={styles.text}>Join</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.closeParent}
                onPress={_ => this.drawer?.close()}>
                  <Image
                    style={styles.close}
                    source={require('../HamburgerClose.png')} />
              </TouchableOpacity>
            </View>
          }
          tapToClose={true}
          openDrawerOffset={0.35}
          side='right'
          styles={DrawerStyles}
          tweenDuration={(100)}
          tweenEasing={'easeInQuad'}
          acceptPan={false}
      >
        <View 
        style={styles.container} >
          <TouchableOpacity
            style={styles.button}
            onPress={_ => this.navigation.push('Settings')}

          >
            <Text style={styles.text}>Join</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.button}
            onPress={_ => this.navigation.push('Settings')}

          >
            <Text style={styles.text}>Create</Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={_ => this.drawer?.open()}
            style={styles.logoParent}
          >
            <Image
            style={{...styles.logo, display: this.drawer?.state ? 'none' : 'flex'}}
            source={require('../Hamburger.png')}
          />
          </TouchableOpacity>
        </View>
      </Drawer>
    );
  }
}

const DrawerStyles = {
  drawer: {
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    borderRightWidth: 2,
    borderColor: 'rgba(34, 34, 34, 1)',
    shadowColor: '#000',
    shadowOpacity: 0.58,
    shadowOffset: {
      width: 5,
      height: 0
    }
  },
  main: {

  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: Colors.dark.background
  },
  sideBarParent: {
    alignItems: 'center'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  logo: {
    position: 'absolute',
    top: 15,
    right: 15,
  },
  logoParent: {
    position: 'absolute',
    top: 0,
    right: 0,
    width: 65,
    height: 60
  },
  close: {
    position: 'absolute',
    top: 15,
    left: 15
  },
  closeParent: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: 60,
    height: 60
  },
  text: {
    fontFamily: 'Roboto',
    fontSize: 40,

    width: 250,
    height: 40,
    textAlignVertical: 'center',
    textAlign: 'center',
  },
  button: {
    backgroundColor: '#DDDDDD',
    width: 250,
    height: 55,
    margin: 12.5,
    borderRadius: 10,
    justifyContent: 'center'
  },
  sidebarbutton: {
    backgroundColor: '#DDDDDD',
    width: 175,
    height: 55,
    margin: 12.5,
    borderRadius: 10,
    textAlign: 'center',
    alignItems: 'center', 
    justifyContent: 'center'
  },
});
